mapper.select:

select BUYER_CONSIGNEE_ID, NAME, MOBILE, CITY_ID, ADDRESS, BUYER_ID, IS_DEFAULT, CREATE_TIME
from rc_buyer_consignee
where BUYER_CONSIGNEE_ID = #{consigneeId,jdbcType=INTEGER}


    select BUYER_CONSIGNEE_ID, NAME, MOBILE, CITY_ID, ADDRESS, BUYER_ID, IS_DEFAULT, CREATE_TIME
    from rc_buyer_consignee


        and NAME = #{name,jdbcType=VARCHAR}


        and MOBILE = #{mobile,jdbcType=VARCHAR}


        and CITY_ID = #{cityId,jdbcType=INTEGER}


        and ADDRESS = #{address,jdbcType=VARCHAR}


        and BUYER_ID = #{memberId,jdbcType=INTEGER}


        and IS_DEFAULT = #{isDefault,jdbcType=BIT}


        and CREATE_TIME = #{createTime,jdbcType=TIMESTAMP}



	  order by ${orderBy}


select BUYER_CONSIGNEE_ID, NAME, MOBILE, CITY_ID, ADDRESS, BUYER_ID, IS_DEFAULT, CREATE_TIME
from rc_buyer_consignee


    and NAME = #{name,jdbcType=VARCHAR}


    and MOBILE = #{mobile,jdbcType=VARCHAR}


    and CITY_ID = #{cityId,jdbcType=INTEGER}


    and ADDRESS = #{address,jdbcType=VARCHAR}


    and BUYER_ID = #{memberId,jdbcType=INTEGER}


    and IS_DEFAULT = #{isDefault,jdbcType=BIT}


    and CREATE_TIME = #{createTime,jdbcType=TIMESTAMP}


limit 1

mapper.delete:

delete from rc_buyer_consignee


    and NAME = #{name,jdbcType=VARCHAR}


    and MOBILE = #{mobile,jdbcType=VARCHAR}


    and CITY_ID = #{cityId,jdbcType=INTEGER}


    and ADDRESS = #{address,jdbcType=VARCHAR}


    and BUYER_ID = #{memberId,jdbcType=INTEGER}


    and IS_DEFAULT = #{isDefault,jdbcType=BIT}


    and CREATE_TIME = #{createTime,jdbcType=TIMESTAMP}
