mapper.select:

    select BUYER_COUPON_ID, COUPON_ID, BUYER_ID, CREATE_TIME, STATUS, EFFECT_TIME, 
    EXPIRE_TIME
    from rc_buyer_coupon


        and COUPON_ID = #{couponId,jdbcType=INTEGER}



	      COUPON_ID = #{cId,jdbcType=INTEGER}



        and BUYER_ID = #{buyerId,jdbcType=INTEGER}


        and CREATE_TIME = #{createTime,jdbcType=TIMESTAMP}


        and STATUS = #{status,jdbcType=INTEGER}


        and EFFECT_TIME = #{effectTime,jdbcType=TIMESTAMP}


        and EXPIRE_TIME = #{expireTime,jdbcType=TIMESTAMP}


        and EXPIRE_TIME > #{expireTimeFrom,jdbcType=TIMESTAMP}


        and EXPIRE_TIME <= #{expireTimeTo,jdbcType=TIMESTAMP}



	  order by ${orderBy}


select BUYER_COUPON_ID, COUPON_ID, BUYER_ID, CREATE_TIME, STATUS, EFFECT_TIME, 
EXPIRE_TIME
from rc_buyer_coupon


    and COUPON_ID = #{couponId,jdbcType=INTEGER}


    and BUYER_ID = #{buyerId,jdbcType=INTEGER}


    and CREATE_TIME = #{createTime,jdbcType=TIMESTAMP}


    and STATUS = #{status,jdbcType=INTEGER}


    and EFFECT_TIME = #{effectTime,jdbcType=TIMESTAMP}


    and EXPIRE_TIME = #{expireTime,jdbcType=TIMESTAMP}


limit 1

mapper.delete:

delete from rc_buyer_coupon


    and COUPON_ID = #{couponId,jdbcType=INTEGER}


    and BUYER_ID = #{buyerId,jdbcType=INTEGER}


    and CREATE_TIME = #{createTime,jdbcType=TIMESTAMP}


    and STATUS = #{status,jdbcType=INTEGER}


    and EFFECT_TIME = #{effectTime,jdbcType=TIMESTAMP}


    and EXPIRE_TIME = #{expireTime,jdbcType=TIMESTAMP}
