mapper.select:

    select BUYER_ID, ACCOUNT, PASSWORD, NAME, EMAIL, SEX, MOBILE, IMG_URL, DESCRIPTION, 
    LAST_LOGIN_TIME, SIGN_UP_TIME, DEl_FLAG, WALLET_AMOUNT, WALLET_PWD
    from rc_buyer


        and ACCOUNT like CONCAT('%', #{account,jdbcType=VARCHAR}, '%')


        and PASSWORD = #{password,jdbcType=VARCHAR}


        and NAME like CONCAT('%', #{name,jdbcType=VARCHAR}, '%')


        and EMAIL like CONCAT('%', #{email,jdbcType=VARCHAR}, '%')


        and SEX = #{sex,jdbcType=INTEGER}


        and MOBILE like CONCAT('%', #{mobile,jdbcType=VARCHAR}, '%')


        and IMG_URL = #{imgUrl,jdbcType=VARCHAR}


        and DESCRIPTION = #{description,jdbcType=VARCHAR}


        and LAST_LOGIN_TIME = #{lastLoginTime,jdbcType=TIMESTAMP}


        and SIGN_UP_TIME = #{signUpTime,jdbcType=TIMESTAMP}


        and DEl_FLAG = #{delFlag,jdbcType=BIT}


        and WALLET_AMOUNT = #{walletAmount,jdbcType=DECIMAL}


        and WALLET_PWD = #{walletPwd,jdbcType=VARCHAR}



	  order by ${orderBy}


    select BUYER_ID, ACCOUNT, PASSWORD, NAME, EMAIL, SEX, MOBILE, IMG_URL, DESCRIPTION, 
    LAST_LOGIN_TIME, SIGN_UP_TIME, DEl_FLAG, WALLET_AMOUNT, WALLET_PWD
    from rc_buyer


        and ACCOUNT = #{account,jdbcType=VARCHAR}


        and PASSWORD = #{password,jdbcType=VARCHAR}


        and NAME = #{name,jdbcType=VARCHAR}


        and EMAIL = #{email,jdbcType=VARCHAR}


        and SEX = #{sex,jdbcType=INTEGER}


        and MOBILE = #{mobile,jdbcType=VARCHAR}


        and IMG_URL = #{imgUrl,jdbcType=VARCHAR}


        and DESCRIPTION = #{description,jdbcType=VARCHAR}


        and LAST_LOGIN_TIME = #{lastLoginTime,jdbcType=TIMESTAMP}


        and SIGN_UP_TIME = #{signUpTime,jdbcType=TIMESTAMP}


        and DEl_FLAG = #{delFlag,jdbcType=BIT}


        and WALLET_AMOUNT = #{walletAmount,jdbcType=DECIMAL}


        and WALLET_PWD = #{walletPwd,jdbcType=VARCHAR}


	limit 1

mapper.delete:

