mapper.select:

    select BUYER_TRADE_ID, AMOUNT, BALANCE, TYPE, ORDER_ID, CREATE_TIME, BUYER_ID, 
    PAYMENT_TYPE, PAYMENT_INTERFACE
    from rc_buyer_trade


        and AMOUNT = #{amount,jdbcType=DECIMAL}


        and BALANCE = #{balance,jdbcType=DECIMAL}


        and TYPE = #{type,jdbcType=INTEGER}


        and ORDER_ID = #{orderId,jdbcType=BIGINT}


        and CREATE_TIME = #{createTime,jdbcType=TIMESTAMP}


        and BUYER_ID = #{buyerId,jdbcType=INTEGER}


        and PAYMENT_TYPE = #{paymentType,jdbcType=INTEGER}


        and PAYMENT_INTERFACE = #{paymentInterface,jdbcType=INTEGER}



	  order by ${orderBy}


select BUYER_TRADE_ID, AMOUNT, BALANCE, TYPE, ORDER_ID, CREATE_TIME, BUYER_ID, 
PAYMENT_TYPE, PAYMENT_INTERFACE
from rc_buyer_trade


    and AMOUNT = #{amount,jdbcType=DECIMAL}


    and BALANCE = #{balance,jdbcType=DECIMAL}


    and TYPE = #{type,jdbcType=INTEGER}


    and ORDER_ID = #{orderId,jdbcType=BIGINT}


    and CREATE_TIME = #{createTime,jdbcType=TIMESTAMP}


    and BUYER_ID = #{buyerId,jdbcType=INTEGER}


    and PAYMENT_TYPE = #{paymentType,jdbcType=INTEGER}


    and PAYMENT_INTERFACE = #{paymentInterface,jdbcType=INTEGER}


limit 1

mapper.delete:

delete from rc_buyer_trade


    and AMOUNT = #{amount,jdbcType=DECIMAL}


    and BALANCE = #{balance,jdbcType=DECIMAL}


    and TYPE = #{type,jdbcType=INTEGER}


    and ORDER_ID = #{orderId,jdbcType=BIGINT}


    and CREATE_TIME = #{createTime,jdbcType=TIMESTAMP}


    and BUYER_ID = #{buyerId,jdbcType=INTEGER}


    and PAYMENT_TYPE = #{paymentType,jdbcType=INTEGER}


    and PAYMENT_INTERFACE = #{paymentInterface,jdbcType=INTEGER}
