mapper.select:

    select CITY_ID, PROVINCE_NAME, NAME, FREIGHT, FREIGHT_BASE_AMOUNT, IS_DREDGED, 
    IS_HOT, DREDGE_TIME, IS_DEFAULT
    from rc_city


        and PROVINCE_NAME like CONCAT('%', #{provinceName,jdbcType=VARCHAR}, '%')


        and NAME like CONCAT('%', #{name,jdbcType=VARCHAR}, '%')


        and FREIGHT = #{freight,jdbcType=DECIMAL}


        and FREIGHT_BASE_AMOUNT = #{freightBaseAmount,jdbcType=DECIMAL}


        and IS_DREDGED = #{isDredged,jdbcType=BIT}


        and IS_HOT = #{isHot,jdbcType=BIT}


        and DREDGE_TIME = #{dredgeTime,jdbcType=TIMESTAMP}


        and IS_DEFAULT = #{isDefault,jdbcType=BIT}



	  order by ${orderBy}


select CITY_ID, PROVINCE_NAME, NAME, FREIGHT, FREIGHT_BASE_AMOUNT, IS_DREDGED, 
IS_HOT, DREDGE_TIME, IS_DEFAULT
from rc_city


    and PROVINCE_NAME = #{provinceName,jdbcType=VARCHAR}


    and NAME = #{name,jdbcType=VARCHAR}


    and FREIGHT = #{freight,jdbcType=DECIMAL}


    and FREIGHT_BASE_AMOUNT = #{freightBaseAmount,jdbcType=DECIMAL}


    and IS_DREDGED = #{isDredged,jdbcType=BIT}


    and IS_HOT = #{isHot,jdbcType=BIT}


    and DREDGE_TIME = #{dredgeTime,jdbcType=TIMESTAMP}


    and IS_DEFAULT = #{isDefault,jdbcType=BIT}


limit 1

mapper.delete:

delete from rc_city


    and PROVINCE_NAME = #{provinceName,jdbcType=VARCHAR}


    and NAME = #{name,jdbcType=VARCHAR}


    and FREIGHT = #{freight,jdbcType=DECIMAL}


    and FREIGHT_BASE_AMOUNT = #{freightBaseAmount,jdbcType=DECIMAL}


    and IS_DREDGED = #{isDredged,jdbcType=BIT}


    and IS_HOT = #{isHot,jdbcType=BIT}


    and DREDGE_TIME = #{dredgeTime,jdbcType=TIMESTAMP}


    and IS_DEFAULT = #{isDefault,jdbcType=BIT}
