mapper.select:

    select COUPON_ID, AMOUNT, CREATE_TIME, INIT_COUNT, LEFT_COUNT, EFFECT_TIME, 
    EXPIRE_TIME, MERCHANT_ID, PROD_CAT_ID, DEL_FLAG, NAME, BASE_AMOUNT, 
    DESCRIPTION, STATUS, RECEIVE_START_TIME, RECEIVE_END_TIME,NEED_GET,PROD_ID,DISCOUNT_TYPE
    from rc_coupon


        and AMOUNT = #{amount,jdbcType=DECIMAL}


        and AMOUNT >= #{amountFrom,jdbcType=DECIMAL}


        and AMOUNT <= #{amountTo,jdbcType=DECIMAL}


        and CREATE_TIME = #{createTime,jdbcType=TIMESTAMP}


        and CREATE_TIME >= #{createTimeFrom,jdbcType=DECIMAL}


        and CREATE_TIME <= #{createTimeTo,jdbcType=DECIMAL}


        and INIT_COUNT = #{initCount,jdbcType=INTEGER}


        and LEFT_COUNT = #{leftCount,jdbcType=INTEGER}


        and EFFECT_TIME = #{effectTime,jdbcType=TIMESTAMP}


        and EXPIRE_TIME = #{expireTime,jdbcType=TIMESTAMP}


        and MERCHANT_ID = #{merchantId,jdbcType=INTEGER}



	      MERCHANT_ID = #{mId,jdbcType=INTEGER}



        and PROD_CAT_ID = #{prodCatId,jdbcType=INTEGER}


        and DEL_FLAG = #{delFlag,jdbcType=BIT}


        and NAME = #{name,jdbcType=VARCHAR}


        and BASE_AMOUNT = #{baseAmount,jdbcType=DECIMAL}


        and DESCRIPTION = #{description,jdbcType=VARCHAR}


        and STATUS = #{status,jdbcType=INTEGER}



	      STATUS = #{sta,jdbcType=INTEGER}



        and RECEIVE_START_TIME = #{receiveStartTime,jdbcType=TIMESTAMP}


        and RECEIVE_END_TIME = #{receiveEndTime,jdbcType=TIMESTAMP}


        and RECEIVE_END_TIME > #{receiveEndTimeFrom,jdbcType=TIMESTAMP}


        and NEED_GET = #{needGet,jdbcType=BIT}


        and PROD_ID = #{prodId,jdbcType=INTEGER}


        and DISCOUNT_TYPE = #{discountType,jdbcType=INTEGER}



	  order by ${orderBy}


select COUPON_ID, AMOUNT, CREATE_TIME, INIT_COUNT, LEFT_COUNT, EFFECT_TIME, 
EXPIRE_TIME, MERCHANT_ID, PROD_CAT_ID, DEL_FLAG, NAME, BASE_AMOUNT, 
DESCRIPTION, STATUS, RECEIVE_START_TIME, RECEIVE_END_TIME,NEED_GET,PROD_ID,DISCOUNT_TYPE
from rc_coupon


    and AMOUNT = #{amount,jdbcType=DECIMAL}


    and CREATE_TIME = #{createTime,jdbcType=TIMESTAMP}


    and INIT_COUNT = #{initCount,jdbcType=INTEGER}


    and LEFT_COUNT = #{leftCount,jdbcType=INTEGER}


    and EFFECT_TIME = #{effectTime,jdbcType=TIMESTAMP}


    and EXPIRE_TIME = #{expireTime,jdbcType=TIMESTAMP}


    and MERCHANT_ID = #{merchantId,jdbcType=INTEGER}


    and PROD_CAT_ID = #{prodCatId,jdbcType=INTEGER}


    and DEL_FLAG = #{delFlag,jdbcType=BIT}


    and NAME = #{name,jdbcType=VARCHAR}


    and BASE_AMOUNT = #{baseAmount,jdbcType=DECIMAL}


    and DESCRIPTION = #{description,jdbcType=VARCHAR}


    and STATUS = #{status,jdbcType=INTEGER}


    and RECEIVE_START_TIME = #{receiveStartTime,jdbcType=TIMESTAMP}


    and RECEIVE_END_TIME = #{receiveEndTime,jdbcType=TIMESTAMP}


    and NEED_GET = #{needGet,jdbcType=BIT}


    and PROD_ID = #{prodId,jdbcType=INTEGER}


    and DISCOUNT_TYPE = #{discountType,jdbcType=INTEGER}


limit 1

