mapper.insert:

insert into rc_file_info (FILE_INFO_ID, ORIGINAL_NAME, REAL_NAME, SUFFIX_NAME, 
  SIZE, RELATIVE_PATH, CREATE_TIME, MEMBER_ID
  )
values (#{fileInfoId,jdbcType=VARCHAR}, #{originalName,jdbcType=VARCHAR}, #{realName,jdbcType=VARCHAR}, #{suffixName,jdbcType=VARCHAR}, 
  #{size,jdbcType=BIGINT}, #{relativePath,jdbcType=VARCHAR}, #{createTime,jdbcType=TIMESTAMP}, #{memberId,jdbcType=INTEGER}
  )

mapper.select:

    select FILE_INFO_ID, ORIGINAL_NAME, REAL_NAME, SUFFIX_NAME, SIZE, 
      RELATIVE_PATH, CREATE_TIME, MEMBER_ID
    from rc_file_info


        and ORIGINAL_NAME = #{originalName,jdbcType=VARCHAR}


        and REAL_NAME = #{realName,jdbcType=VARCHAR}


        and SUFFIX_NAME = #{suffixName,jdbcType=VARCHAR}


        and SIZE = #{size,jdbcType=BIGINT}


        and RELATIVE_PATH = #{relativePath,jdbcType=VARCHAR}


        and CREATE_TIME = #{createTime,jdbcType=TIMESTAMP}


        and MEMBER_ID = #{memberId,jdbcType=INTEGER}



	  order by ${orderBy}


select FILE_INFO_ID, ORIGINAL_NAME, REAL_NAME, SUFFIX_NAME, SIZE, 
  RELATIVE_PATH, CREATE_TIME, MEMBER_ID
from rc_file_info


    and ORIGINAL_NAME = #{originalName,jdbcType=VARCHAR}


    and REAL_NAME = #{realName,jdbcType=VARCHAR}


    and SUFFIX_NAME = #{suffixName,jdbcType=VARCHAR}


    and SIZE = #{size,jdbcType=BIGINT}


    and RELATIVE_PATH = #{relativePath,jdbcType=VARCHAR}


    and CREATE_TIME = #{createTime,jdbcType=TIMESTAMP}


    and MEMBER_ID = #{memberId,jdbcType=INTEGER}


limit 1

