mapper.select:

    select IMAGE_SPACE_ID, FILE_INFO_ID, MEMBER_ID, DEL_FLAG, PARENT_ID, TYPE, NAME, 
    DEL_TIME, IMG_WIDTH, IMG_HEIGHT, IMG_SIZE, CREATE_TIME
    from rc_image_space


        and FILE_INFO_ID = #{fileInfoId,jdbcType=VARCHAR}


        and MEMBER_ID = #{memberId,jdbcType=INTEGER}


        and DEL_FLAG = #{delFlag,jdbcType=BIT}


        and PARENT_ID = #{parentId,jdbcType=BIGINT}


        and TYPE = #{type,jdbcType=INTEGER}


        and NAME like CONCAT('%', #{name,jdbcType=VARCHAR}, '%')


        and DEL_TIME = #{delTime,jdbcType=TIMESTAMP}


        and IMG_WIDTH = #{imgWidth,jdbcType=INTEGER}


        and IMG_HEIGHT = #{imgHeight,jdbcType=INTEGER}


        and IMG_SIZE = #{imgSize,jdbcType=BIGINT}


        and CREATE_TIME = #{createTime,jdbcType=TIMESTAMP}



	  order by ${orderBy}


select IMAGE_SPACE_ID, FILE_INFO_ID, MEMBER_ID, DEL_FLAG, PARENT_ID, TYPE, NAME, 
DEL_TIME, IMG_WIDTH, IMG_HEIGHT, IMG_SIZE, CREATE_TIME
from rc_image_space


    and FILE_INFO_ID = #{fileInfoId,jdbcType=VARCHAR}


    and MEMBER_ID = #{memberId,jdbcType=INTEGER}


    and DEL_FLAG = #{delFlag,jdbcType=BIT}


    and PARENT_ID = #{parentId,jdbcType=BIGINT}


    and TYPE = #{type,jdbcType=INTEGER}


    and NAME = #{name,jdbcType=VARCHAR}


    and DEL_TIME = #{delTime,jdbcType=TIMESTAMP}


    and IMG_WIDTH = #{imgWidth,jdbcType=INTEGER}


    and IMG_HEIGHT = #{imgHeight,jdbcType=INTEGER}


    and IMG_SIZE = #{imgSize,jdbcType=BIGINT}


    and CREATE_TIME = #{createTime,jdbcType=TIMESTAMP}


limit 1

mapper.delete:

delete from rc_image_space


    and FILE_INFO_ID = #{fileInfoId,jdbcType=VARCHAR}


    and MEMBER_ID = #{memberId,jdbcType=INTEGER}


    and DEL_FLAG = #{delFlag,jdbcType=BIT}


    and PARENT_ID = #{parentId,jdbcType=BIGINT}


    and TYPE = #{type,jdbcType=INTEGER}


    and NAME = #{name,jdbcType=VARCHAR}


    and DEL_TIME = #{delTime,jdbcType=TIMESTAMP}


    and IMG_WIDTH = #{imgWidth,jdbcType=INTEGER}


    and IMG_HEIGHT = #{imgHeight,jdbcType=INTEGER}


    and IMG_SIZE = #{imgSize,jdbcType=BIGINT}


    and CREATE_TIME = #{createTime,jdbcType=TIMESTAMP}
