    and TABLE_ID = #{tableId,jdbcType=INTEGER}


    and SECTION_NAME = #{sectionName,jdbcType=VARCHAR}


    and TABLE_NUMBER = #{tableNumber,jdbcType=INTEGER}


    and IMG_URL = #{imgUrl,jdbcType=VARCHAR}


