mapper.select:

    select ORDER_ID, TRACKING_NUMBER, LOGISTICS_COMPANY, DELIVERY_TYPE, DELIVERY_TIME, 
    DISPATCHER_MOBILE, DISPATCHER_NAME
    from rc_order_delivery


        and TRACKING_NUMBER = #{trackingNumber,jdbcType=VARCHAR}


        and LOGISTICS_COMPANY = #{logisticsCompany,jdbcType=VARCHAR}


        and DELIVERY_TYPE = #{deliveryType,jdbcType=INTEGER}


        and DELIVERY_TIME = #{deliveryTime,jdbcType=TIMESTAMP}


        and DISPATCHER_MOBILE = #{dispatcherMobile,jdbcType=VARCHAR}


        and DISPATCHER_NAME = #{dispatcherName,jdbcType=VARCHAR}



	  order by ${orderBy}


select ORDER_ID, TRACKING_NUMBER, LOGISTICS_COMPANY, DELIVERY_TYPE, DELIVERY_TIME, 
DISPATCHER_MOBILE, DISPATCHER_NAME
from rc_order_delivery


    and TRACKING_NUMBER = #{trackingNumber,jdbcType=VARCHAR}


    and LOGISTICS_COMPANY = #{logisticsCompany,jdbcType=VARCHAR}


    and DELIVERY_TYPE = #{deliveryType,jdbcType=INTEGER}


    and ORDER_ID = #{orderId}


    and DELIVERY_TIME = #{deliveryTime,jdbcType=TIMESTAMP}


    and DISPATCHER_MOBILE = #{dispatcherMobile,jdbcType=VARCHAR}


    and DISPATCHER_NAME = #{dispatcherName,jdbcType=VARCHAR}


limit 1

mapper.delete:

