mapper.select:

    select ORDER_ID, BUYER_ID, CREATE_TIME, PRODUCT_PRICE, PAY_PRICE, BUYER_STATUS, 
    SELLER_STATUS, MERCHANT_ID, DELIVERY_TYPE, PAYMENT_INTERFACE, PAYMENT_TYPE, PICKUP_CODE, 
    SELLER_MEMO, BUYER_MEMO, VENDOR_ID, CITY_ID, TABLE_NUMBER, CUSTOMER_COUNT, EAT_TYPE, SECTION_NAME, TABLE_ID,SECTION_ID
    from rc_order



	      ORDER_ID = #{oId,jdbcType=INTEGER}



        and BUYER_ID = #{memberId,jdbcType=INTEGER}



	      BUYER_ID = #{mId,jdbcType=INTEGER}




          SECTION_ID = #{secId,jdbcType=INTEGER}



        and CREATE_TIME = #{createTime,jdbcType=TIMESTAMP}


        and CREATE_TIME >= #{createTimeFrom,jdbcType=TIMESTAMP}


        and CREATE_TIME <= #{createTimeTo,jdbcType=TIMESTAMP}


        and PRODUCT_PRICE = #{productPrice,jdbcType=DECIMAL}


        and PAY_PRICE = #{payPrice,jdbcType=DECIMAL}


        and BUYER_STATUS = #{buyerStatus,jdbcType=INTEGER}


        and SELLER_STATUS = #{sellerStatus,jdbcType=INTEGER}



	      SELLER_STATUS = #{ssta,jdbcType=INTEGER}



        and MERCHANT_ID = #{merchantId,jdbcType=INTEGER}



	      MERCHANT_ID = #{mId,jdbcType=INTEGER}



        and DELIVERY_TYPE = #{deliveryType,jdbcType=INTEGER}


        and PAYMENT_INTERFACE = #{paymentInterface,jdbcType=INTEGER}


        and PAYMENT_TYPE = #{paymentType,jdbcType=INTEGER}


        and PICKUP_CODE = #{pickupCode,jdbcType=VARCHAR}


        and SELLER_MEMO = #{sellerMemo,jdbcType=VARCHAR},


        and BUYER_MEMO = #{buyerMemo,jdbcType=VARCHAR}


        and VENDOR_ID = #{vendorId,jdbcType=INTEGER}


        and CITY_ID = #{cityId,jdbcType=INTEGER}



	      CITY_ID = #{cId,jdbcType=INTEGER}



        and TABLE_NUMBER = #{tableNumber,jdbcType=INTEGER}


        and CUSTOMER_COUNT = #{customerCount,jdbcType=INTEGER}


        and EAT_TYPE = #{eatType,jdbcType=INTEGER}


        and SECTION_NAME = #{sectionName,jdbcType=VARCHAR}


        and TABLE_ID = #{tableId,jdbcType=INTEGER}



	  order by ${orderBy}


select ORDER_ID, BUYER_ID, CREATE_TIME, PRODUCT_PRICE, PAY_PRICE, BUYER_STATUS, 
SELLER_STATUS, MERCHANT_ID, DELIVERY_TYPE, PAYMENT_INTERFACE, PAYMENT_TYPE, PICKUP_CODE, 
SELLER_MEMO, BUYER_MEMO, VENDOR_ID, CITY_ID, TABLE_NUMBER, CUSTOMER_COUNT, EAT_TYPE, SECTION_NAME, TABLE_ID,SECTION_ID
from rc_order


    and BUYER_ID = #{memberId,jdbcType=INTEGER}


    and CREATE_TIME = #{createTime,jdbcType=TIMESTAMP}


    and PRODUCT_PRICE = #{productPrice,jdbcType=DECIMAL}


    and PAY_PRICE = #{payPrice,jdbcType=DECIMAL}


    and BUYER_STATUS = #{buyerStatus,jdbcType=INTEGER}


    and SECTION_ID = #{sectionId,jdbcType=INTEGER}


    and SELLER_STATUS = #{sellerStatus,jdbcType=INTEGER}


    and MERCHANT_ID = #{merchantId,jdbcType=INTEGER}


    and DELIVERY_TYPE = #{deliveryType,jdbcType=INTEGER}


    and PAYMENT_INTERFACE = #{paymentInterface,jdbcType=INTEGER}


    and PAYMENT_TYPE = #{paymentType,jdbcType=INTEGER}


    and PICKUP_CODE = #{pickupCode,jdbcType=VARCHAR}


    and SELLER_MEMO = #{sellerMemo,jdbcType=VARCHAR},


    and BUYER_MEMO = #{buyerMemo,jdbcType=VARCHAR}


    and VENDOR_ID = #{vendorId,jdbcType=INTEGER}


    and CITY_ID = #{cityId,jdbcType=INTEGER}


    and TABLE_NUMBER = #{tableNumber,jdbcType=INTEGER}


    and CUSTOMER_COUNT = #{customerCount,jdbcType=INTEGER}


    and EAT_TYPE = #{eatType,jdbcType=INTEGER}


    and SECTION_NAME = #{sectionName,jdbcType=VARCHAR}


    and TABLE_ID = #{tableId,jdbcType=INTEGER}


limit 1

