mapper.select:

select ORDER_OPERATION_ID, ORDER_ID, OPERATION_TYPE, MEMBER_ID, OPERATION_MEMO, OPERATION_TIME
from rc_order_operation
where ORDER_OPERATION_ID = #{orderOperationId,jdbcType=BIGINT}


    select ORDER_OPERATION_ID, ORDER_ID, OPERATION_TYPE, MEMBER_ID, OPERATION_MEMO, OPERATION_TIME
    from rc_order_operation


        and ORDER_ID = #{orderId,jdbcType=BIGINT}


        and OPERATION_TYPE = #{operationType,jdbcType=INTEGER}


        and MEMBER_ID = #{memberId,jdbcType=INTEGER}


        and OPERATION_MEMO = #{operationMemo,jdbcType=VARCHAR}


        and OPERATION_TIME = #{operationTime,jdbcType=TIMESTAMP}


        and OPERATION_TIME <= #{operationTimeTo,jdbcType=TIMESTAMP}



	  order by ${orderBy}


select ORDER_OPERATION_ID, ORDER_ID, OPERATION_TYPE, MEMBER_ID, OPERATION_MEMO, OPERATION_TIME
from rc_order_operation


    and ORDER_ID = #{orderId,jdbcType=BIGINT}


    and OPERATION_TYPE = #{operationType,jdbcType=INTEGER}


    and MEMBER_ID = #{memberId,jdbcType=INTEGER}


    and OPERATION_MEMO = #{operationMemo,jdbcType=VARCHAR}


    and OPERATION_TIME = #{operationTime,jdbcType=TIMESTAMP}


limit 1

mapper.delete:

delete from rc_order_operation


    and ORDER_ID = #{orderId,jdbcType=BIGINT}


    and OPERATION_TYPE = #{operationType,jdbcType=INTEGER}


    and MEMBER_ID = #{memberId,jdbcType=INTEGER}


    and OPERATION_MEMO = #{operationMemo,jdbcType=VARCHAR}


    and OPERATION_TIME = #{operationTime,jdbcType=TIMESTAMP}
