mapper.select:

    select ORDER_PAYMENT_ID, ORDER_ID, PAYMENT_INTERFACE, PAYMENT_TYPE, 
      TRANSACTION_ID, THIRD_PARTY_TRANSACTION_ID, PAY_CODE, AMOUNT, STATUS, 
      CREATE_TIME, FINISH_TIME
    from rc_order_payment


        and ORDER_ID = #{orderId,jdbcType=BIGINT}


        and PAYMENT_INTERFACE = #{paymentInterface,jdbcType=INTEGER}


        and PAYMENT_TYPE = #{paymentType,jdbcType=INTEGER}


        and TRANSACTION_ID = #{transactionId,jdbcType=VARCHAR}


        and THIRD_PARTY_TRANSACTION_ID = #{thirdPartyTransactionId,jdbcType=VARCHAR}


        and PAY_CODE = #{payCode,jdbcType=VARCHAR}


        and AMOUNT = #{amount,jdbcType=DECIMAL}


        and STATUS = #{status,jdbcType=INTEGER}


        and CREATE_TIME = #{createTime,jdbcType=TIMESTAMP}


        and FINISH_TIME = #{finishTime,jdbcType=TIMESTAMP}



	  order by ${orderBy}


    select ORDER_PAYMENT_ID, ORDER_ID, PAYMENT_INTERFACE, PAYMENT_TYPE, 
      TRANSACTION_ID, THIRD_PARTY_TRANSACTION_ID, PAY_CODE, AMOUNT, STATUS, 
      CREATE_TIME, FINISH_TIME
    from rc_order_payment


        and ORDER_ID = #{orderId,jdbcType=BIGINT}


        and PAYMENT_INTERFACE = #{paymentInterface,jdbcType=INTEGER}


        and PAYMENT_TYPE = #{paymentType,jdbcType=INTEGER}


        and TRANSACTION_ID = #{transactionId,jdbcType=VARCHAR}


        and THIRD_PARTY_TRANSACTION_ID = #{thirdPartyTransactionId,jdbcType=VARCHAR}


        and PAY_CODE = #{payCode,jdbcType=VARCHAR}


        and AMOUNT = #{amount,jdbcType=DECIMAL}


        and STATUS = #{status,jdbcType=INTEGER}


        and CREATE_TIME = #{createTime,jdbcType=TIMESTAMP}


        and FINISH_TIME = #{finishTime,jdbcType=TIMESTAMP}



	  order by ${orderBy}

    limit 1

