mapper.insert:

insert into rc_order_prod_evaluation (ORDER_PROD_ID, PROD_ID, CREATE_TIME, 
  SCORE, IS_ANOY, ORDER_ID, BUYER_ID, MERCHANT_REPLY, 
  PROD_SKU_ID, MERCHANT_ID, VENDOR_ID, CONTENT
  )
values (#{orderProdId,jdbcType=BIGINT}, #{prodId,jdbcType=INTEGER}, #{createTime,jdbcType=TIMESTAMP}, 
  #{score,jdbcType=INTEGER}, #{isAnoy,jdbcType=BIT}, #{orderId,jdbcType=BIGINT}, 
  #{memberId,jdbcType=INTEGER}, #{merchantReply,jdbcType=VARCHAR}, 
  #{prodSkuId,jdbcType=VARCHAR}, #{merchantId,jdbcType=INTEGER}, 
  #{vendorId,jdbcType=INTEGER}, #{content,jdbcType=VARCHAR}
  )

mapper.select:

    select ORDER_PROD_ID, PROD_ID, CREATE_TIME, SCORE, IS_ANOY, ORDER_ID, BUYER_ID, 
    MERCHANT_REPLY, DEL_FLAG, PROD_SKU_ID, MERCHANT_ID, VENDOR_ID, CONTENT, MERCHANT_USER_ID
    from rc_order_prod_evaluation


        and PROD_ID = #{prodId,jdbcType=INTEGER}


        and CREATE_TIME = #{createTime,jdbcType=TIMESTAMP}


        and CREATE_TIME >= #{createTimeFrom,jdbcType=TIMESTAMP}


        and CREATE_TIME <= #{createTimeTo,jdbcType=TIMESTAMP}


        and SCORE = #{score,jdbcType=INTEGER}


        and IS_ANOY = #{isAnoy,jdbcType=BIT}


        and ORDER_ID = #{orderId,jdbcType=BIGINT}


        and BUYER_ID = #{memberId,jdbcType=INTEGER}


        and MERCHANT_REPLY = #{merchantReply,jdbcType=VARCHAR}


        and DEL_FLAG = #{delFlag,jdbcType=BIT}


        and PROD_SKU_ID = #{prodSkuId,jdbcType=VARCHAR}



	      PROD_SKU_ID = #{psId,jdbcType=VARCHAR}



        and MERCHANT_ID = #{merchantId,jdbcType=INTEGER}


        and VENDOR_ID = #{vendorId,jdbcType=INTEGER}


        and CONTENT = #{content,jdbcType=VARCHAR}


        and MERCHANT_USER_ID = #{merchantUserId,jdbcType=INTEGER}



	  order by ${orderBy}


select ORDER_PROD_ID, PROD_ID, CREATE_TIME, SCORE, IS_ANOY, ORDER_ID, BUYER_ID, 
MERCHANT_REPLY, DEL_FLAG, PROD_SKU_ID, MERCHANT_ID, VENDOR_ID, CONTENT, MERCHANT_USER_ID
from rc_order_prod_evaluation


    and PROD_ID = #{prodId,jdbcType=INTEGER}


    and CREATE_TIME = #{createTime,jdbcType=TIMESTAMP}


    and SCORE = #{score,jdbcType=INTEGER}


    and IS_ANOY = #{isAnoy,jdbcType=BIT}


    and ORDER_ID = #{orderId,jdbcType=BIGINT}


    and BUYER_ID = #{memberId,jdbcType=INTEGER}


    and MERCHANT_REPLY = #{merchantReply,jdbcType=VARCHAR}


    and DEL_FLAG = #{delFlag,jdbcType=BIT}


    and PROD_SKU_ID = #{prodSkuId,jdbcType=VARCHAR}


    and MERCHANT_ID = #{merchantId,jdbcType=INTEGER}


    and VENDOR_ID = #{vendorId,jdbcType=INTEGER}


    and CONTENT = #{content,jdbcType=VARCHAR}


    and MERCHANT_USER_ID = #{merchantUserId,jdbcType=INTEGER}


limit 1

