mapper.select:

    select ORDER_PROD_ID, ORDER_ID, PROD_ID, QUANTITY, AMOUNT, PROD_ORIGIN_PRICE, PROD_PRICE, 
    PROD_SKU_ID, PROD_NAME, PRICE_MANNER, MEASURE_UNIT, MEASURE_UNIT_COUNT, STATUS
    from rc_order_prod


        and ORDER_ID = #{orderId,jdbcType=BIGINT}



	      ORDER_ID = #{oId,jdbcType=BIGINT}



        and PROD_ID = #{prodId,jdbcType=INTEGER}



	      PROD_ID = #{pId,jdbcType=INTEGER}



        and QUANTITY = #{quantity,jdbcType=REAL}


        and AMOUNT = #{amount,jdbcType=DECIMAL}


        and PROD_ORIGIN_PRICE = #{prodOriginPrice,jdbcType=DECIMAL}


        and PROD_PRICE = #{prodPrice,jdbcType=DECIMAL}


        and PROD_SKU_ID = #{prodSkuId,jdbcType=VARCHAR}


        and PROD_NAME like CONCAT('%', #{prodName,jdbcType=VARCHAR}, '%')


        and PRICE_MANNER = #{priceManner,jdbcType=INTEGER}


        and MEASURE_UNIT = #{measureUnit,jdbcType=VARCHAR}


        and MEASURE_UNIT_COUNT = #{measureUnitCount,jdbcType=INTEGER}


        and STATUS = #{status,jdbcType=INTEGER}



	  order by ${orderBy}


select ORDER_PROD_ID, ORDER_ID, PROD_ID, QUANTITY, AMOUNT, PROD_ORIGIN_PRICE, PROD_PRICE, 
PROD_SKU_ID, PROD_NAME, PRICE_MANNER, MEASURE_UNIT, MEASURE_UNIT_COUNT, STATUS
from rc_order_prod


    and ORDER_ID = #{orderId,jdbcType=BIGINT}


    and PROD_ID = #{prodId,jdbcType=INTEGER}


    and QUANTITY = #{quantity,jdbcType=REAL}


    and AMOUNT = #{amount,jdbcType=DECIMAL}


    and PROD_ORIGIN_PRICE = #{prodOriginPrice,jdbcType=DECIMAL}


    and PROD_PRICE = #{prodPrice,jdbcType=DECIMAL}


    and PROD_SKU_ID = #{prodSkuId,jdbcType=VARCHAR}


    and PROD_NAME = #{prodName,jdbcType=VARCHAR}


    and PRICE_MANNER = #{priceManner,jdbcType=INTEGER}


    and MEASURE_UNIT = #{measureUnit,jdbcType=VARCHAR}


    and MEASURE_UNIT_COUNT = #{measureUnitCount,jdbcType=INTEGER}


    and STATUS = #{status,jdbcType=INTEGER}


limit 1

mapper.delete:

delete from rc_order_prod


    and ORDER_ID = #{orderId,jdbcType=BIGINT}


    and PROD_ID = #{prodId,jdbcType=INTEGER}


    and QUANTITY = #{quantity,jdbcType=REAL}


    and AMOUNT = #{amount,jdbcType=DECIMAL}


    and PROD_ORIGIN_PRICE = #{prodOriginPrice,jdbcType=DECIMAL}


    and PROD_PRICE = #{prodPrice,jdbcType=DECIMAL}


    and PROD_SKU_ID = #{prodSkuId,jdbcType=VARCHAR}


    and PROD_NAME = #{prodName,jdbcType=VARCHAR}


    and PRICE_MANNER = #{priceManner,jdbcType=INTEGER}


    and MEASURE_UNIT = #{measureUnit,jdbcType=VARCHAR}


    and MEASURE_UNIT_COUNT = #{measureUnitCount,jdbcType=INTEGER}


    and STATUS = #{status,jdbcType=INTEGER}
