mapper.select:

    select PROD_ID, NAME, AD, ORIGIN_PRICE, PRICE, MERCHANT_ID, PROD_CAT_ID, 
    PROD_BRAND_ID, MEASURE_UNIT, TYPE, STOCK_WARNING, DEL_FLAG, SCORE, STATUS, CREATE_TIME, 
    UPDATE_TIME, PUTAWAY_TIME, SOLDOUT_TIME, DETAIL, VENDOR_ID, PROD_BRAND_NAME, CITY_ID, 
    BASE_PROD_ID, INVENTORY, SOLD_NUM, PRICE_MANNER, MEASURE_UNIT_COUNT
    from rc_prod


        and PROD_ID = #{prodId,jdbcType=DECIMAL}


        and NAME like CONCAT('%', #{name,jdbcType=VARCHAR}, '%')


        and AD = #{ad,jdbcType=VARCHAR}


        and ORIGIN_PRICE = #{originPrice,jdbcType=DECIMAL}


        and PRICE = #{price,jdbcType=DECIMAL}


        and MERCHANT_ID = #{merchantId,jdbcType=INTEGER}



	      MERCHANT_ID = #{mId,jdbcType=INTEGER}



        and PROD_CAT_ID = #{prodCatId,jdbcType=INTEGER}


        and PROD_BRAND_ID = #{prodBrandId,jdbcType=INTEGER}


        and MEASURE_UNIT = #{measureUnit,jdbcType=VARCHAR}


        and TYPE = #{type,jdbcType=INTEGER}


        and STOCK_WARNING = #{stockWarning,jdbcType=INTEGER}


        and DEL_FLAG = #{delFlag,jdbcType=BIT}


        and SCORE = #{score,jdbcType=REAL}


        and STATUS = #{status,jdbcType=INTEGER}



	      STATUS = #{sta,jdbcType=INTEGER}



        and CREATE_TIME = #{createTime,jdbcType=TIMESTAMP}


        and UPDATE_TIME = #{updateTime,jdbcType=TIMESTAMP}


        and PUTAWAY_TIME = #{putawayTime,jdbcType=TIMESTAMP}


        and SOLDOUT_TIME = #{soldoutTime,jdbcType=TIMESTAMP}


        and DETAIL = #{detail,jdbcType=LONGVARCHAR}


        and VENDOR_ID = #{vendorId,jdbcType=TIMESTAMP}


        and PROD_BRAND_NAME = #{prodBrandName,jdbcType=TIMESTAMP}


        and CITY_ID = #{cityId,jdbcType=INTEGER}



	      CITY_ID = #{cId,jdbcType=INTEGER}



        and BASE_PROD_ID = #{baseProdId,jdbcType=INTEGER}


        and MERCHANT_ID = 0


        and MERCHANT_ID > 0


        and INVENTORY = #{inventory,jdbcType=INTEGER}


        and SOLD_NUM = #{soldNum,jdbcType=DECIMAL}


        and PRICE_MANNER = #{priceManner,jdbcType=INTEGER}


        and MEASURE_UNIT_COUNT = #{measureUnitCount,jdbcType=INTEGER}



	  order by ${orderBy}


select PROD_ID, NAME, AD, ORIGIN_PRICE, PRICE, MERCHANT_ID, PROD_CAT_ID, 
PROD_BRAND_ID, MEASURE_UNIT, TYPE, STOCK_WARNING, DEL_FLAG, SCORE, STATUS, CREATE_TIME, 
UPDATE_TIME, PUTAWAY_TIME, SOLDOUT_TIME, DETAIL, VENDOR_ID, PROD_BRAND_NAME, CITY_ID, 
BASE_PROD_ID, INVENTORY, SOLD_NUM, PRICE_MANNER, MEASURE_UNIT_COUNT
from rc_prod


    and NAME = #{name,jdbcType=VARCHAR}


    and AD = #{ad,jdbcType=VARCHAR}


    and ORIGIN_PRICE = #{originPrice,jdbcType=DECIMAL}


    and PRICE = #{price,jdbcType=DECIMAL}


    and MERCHANT_ID = #{merchantId,jdbcType=INTEGER}


    and PROD_CAT_ID = #{prodCatId,jdbcType=INTEGER}


    and PROD_BRAND_ID = #{prodBrandId,jdbcType=INTEGER}


    and MEASURE_UNIT = #{measureUnit,jdbcType=VARCHAR}


    and TYPE = #{type,jdbcType=INTEGER}


    and STOCK_WARNING = #{stockWarning,jdbcType=INTEGER}


    and DEL_FLAG = #{delFlag,jdbcType=BIT}


    and SCORE = #{score,jdbcType=REAL}


    and STATUS = #{status,jdbcType=INTEGER}


    and CREATE_TIME = #{createTime,jdbcType=TIMESTAMP}


    and UPDATE_TIME = #{updateTime,jdbcType=TIMESTAMP}


    and PUTAWAY_TIME = #{putawayTime,jdbcType=TIMESTAMP}


    and SOLDOUT_TIME = #{soldoutTime,jdbcType=TIMESTAMP}


    and DETAIL = #{detail,jdbcType=LONGVARCHAR}


    and VENDOR_ID = #{vendorId,jdbcType=TIMESTAMP}


    and PROD_BRAND_NAME = #{prodBrandName,jdbcType=TIMESTAMP}


    and CITY_ID = #{cityId,jdbcType=INTEGER}


    and BASE_PROD_ID = #{baseProdId,jdbcType=INTEGER}


    and INVENTORY = #{inventory,jdbcType=INTEGER}


    and SOLD_NUM = #{soldNum,jdbcType=DECIMAL}


    and PRICE_MANNER = #{priceManner,jdbcType=INTEGER}


    and MEASURE_UNIT_COUNT = #{measureUnitCount,jdbcType=INTEGER}


limit 1

