mapper.insert:


  SELECT LAST_INSERT_ID()

insert into rc_prod_sku_prop_info (PROD_ID, PROD_SKU_PROP_ID, PROD_SKU_ID, 
  PROD_SKU_PROP_NAME, PROD_SKU_PROP_VAL, PROD_SKU_PROP_ENUM_ID, HAS_IMG, 
  IS_PACK_SERVICE
  )
values (#{prodId,jdbcType=INTEGER}, #{prodPropId,jdbcType=INTEGER}, #{prodSkuId,jdbcType=VARCHAR}, 
  #{prodPropName,jdbcType=VARCHAR}, #{prodPropVal,jdbcType=VARCHAR}, #{prodPropEnumId,jdbcType=INTEGER}, 
  #{hasImg,jdbcType=BIT}, #{isPackService,jdbcType=BIT}
  )

mapper.select:

    select PROD_SKU_PROP_INFO_ID, PROD_ID, PROD_SKU_PROP_ID, PROD_SKU_ID, PROD_SKU_PROP_NAME, 
    PROD_SKU_PROP_VAL, PROD_SKU_PROP_ENUM_ID, HAS_IMG, IS_PACK_SERVICE
    from rc_prod_sku_prop_info


        and PROD_ID = #{prodId,jdbcType=INTEGER}


        and PROD_SKU_PROP_ID = #{prodPropId,jdbcType=INTEGER}


        and PROD_SKU_ID = #{prodSkuId,jdbcType=VARCHAR}


        and PROD_SKU_PROP_NAME = #{prodPropName,jdbcType=VARCHAR}


        and PROD_SKU_PROP_VAL = #{prodPropVal,jdbcType=VARCHAR}


        and PROD_SKU_PROP_ENUM_ID = #{prodPropEnumId,jdbcType=INTEGER}


        and HAS_IMG = #{hasImg,jdbcType=BIT}


        and IS_PACK_SERVICE = #{isPackService,jdbcType=BIT}



	  order by ${orderBy}


select PROD_SKU_PROP_INFO_ID, PROD_ID, PROD_SKU_PROP_ID, PROD_SKU_ID, PROD_SKU_PROP_NAME, 
PROD_SKU_PROP_VAL, PROD_SKU_PROP_ENUM_ID, HAS_IMG, IS_PACK_SERVICE
from rc_prod_sku_prop_info


    and PROD_ID = #{prodId,jdbcType=INTEGER}


    and PROD_SKU_PROP_ID = #{prodPropId,jdbcType=INTEGER}


    and PROD_SKU_ID = #{prodSkuId,jdbcType=VARCHAR}


    and PROD_SKU_PROP_NAME = #{prodPropName,jdbcType=VARCHAR}


    and PROD_SKU_PROP_VAL = #{prodPropVal,jdbcType=VARCHAR}


    and PROD_SKU_PROP_ENUM_ID = #{prodPropEnumId,jdbcType=INTEGER}


    and HAS_IMG = #{hasImg,jdbcType=BIT}


    and IS_PACK_SERVICE = #{isPackService,jdbcType=BIT}


limit 1

