mapper.insert:


  SELECT LAST_INSERT_ID()

insert into rc_recharge_record (CREATE_TIME, AMOUNT, MEMBER_ID, 
  RECHARGE_STATION_ID, RECHARGE_MEMBER_ID
  )
values (#{createTime,jdbcType=TIMESTAMP}, #{amount,jdbcType=DECIMAL}, #{memberId,jdbcType=INTEGER}, 
  #{rechargeStationId,jdbcType=INTEGER}, #{rechargeMemberId,jdbcType=INTEGER}
  )

mapper.select:

    select RECHARGE_RECORD_ID, CREATE_TIME, AMOUNT, MEMBER_ID, RECHARGE_STATION_ID, 
    RECHARGE_MEMBER_ID
    from rc_recharge_record


        and CREATE_TIME = #{createTime,jdbcType=TIMESTAMP}


        and CREATE_TIME >= #{createTimeFrom,jdbcType=TIMESTAMP}


        and CREATE_TIME <= #{createTimeTo,jdbcType=TIMESTAMP}


        and AMOUNT = #{amount,jdbcType=DECIMAL}


        and MEMBER_ID = #{memberId,jdbcType=INTEGER}


        and RECHARGE_STATION_ID = #{rechargeStationId,jdbcType=INTEGER}



	      RECHARGE_STATION_ID = #{rsId,jdbcType=INTEGER}



        and RECHARGE_MEMBER_ID = #{rechargeMemberId,jdbcType=INTEGER}



	  order by ${orderBy}


select RECHARGE_RECORD_ID, CREATE_TIME, AMOUNT, MEMBER_ID, RECHARGE_STATION_ID, 
RECHARGE_MEMBER_ID
from rc_recharge_record


    and CREATE_TIME = #{createTime,jdbcType=TIMESTAMP}


    and AMOUNT = #{amount,jdbcType=DECIMAL}


    and MEMBER_ID = #{memberId,jdbcType=INTEGER}


    and RECHARGE_STATION_ID = #{rechargeStationId,jdbcType=INTEGER}


    and RECHARGE_MEMBER_ID = #{rechargeMemberId,jdbcType=INTEGER}


limit 1

mapper.delete:

delete from rc_recharge_record


    and CREATE_TIME = #{createTime,jdbcType=TIMESTAMP}


    and AMOUNT = #{amount,jdbcType=DECIMAL}


    and MEMBER_ID = #{memberId,jdbcType=INTEGER}


    and RECHARGE_STATION_ID = #{rechargeStationId,jdbcType=INTEGER}


    and RECHARGE_MEMBER_ID = #{rechargeMemberId,jdbcType=INTEGER}
