mapper.select:

    select RECHARGE_STATION_ID, MOBILE, STATION_NAME, STATION_LOCATION, CREATE_TIME, 
    DESCRIPTION, MEMBER_ID, CITY_ID
    from rc_recharge_station


        and MOBILE = #{mobile,jdbcType=VARCHAR}


        and STATION_NAME like CONCAT('%', #{stationName,jdbcType=VARCHAR}, '%')


        and STATION_LOCATION = #{stationLocation,jdbcType=VARCHAR}


        and CREATE_TIME = #{createTime,jdbcType=TIMESTAMP}


        and DESCRIPTION = #{description,jdbcType=VARCHAR}


        and MEMBER_ID = #{memberId,jdbcType=INTEGER}


        and CITY_ID = #{cityId,jdbcType=INTEGER}



	      CITY_ID = #{cId,jdbcType=INTEGER}




	  order by ${orderBy}


select RECHARGE_STATION_ID, MOBILE, STATION_NAME, STATION_LOCATION, CREATE_TIME, 
DESCRIPTION, MEMBER_ID, CITY_ID
from rc_recharge_station


    and MOBILE = #{mobile,jdbcType=VARCHAR}


    and STATION_NAME = #{stationName,jdbcType=VARCHAR}


    and STATION_LOCATION = #{stationLocation,jdbcType=VARCHAR}


    and CREATE_TIME = #{createTime,jdbcType=TIMESTAMP}


    and DESCRIPTION = #{description,jdbcType=VARCHAR}


    and MEMBER_ID = #{memberId,jdbcType=INTEGER}


    and CITY_ID = #{cityId,jdbcType=INTEGER}


limit 1

mapper.delete:

delete from rc_recharge_station


    and MOBILE = #{mobile,jdbcType=VARCHAR}


    and STATION_NAME = #{stationName,jdbcType=VARCHAR}


    and STATION_LOCATION = #{stationLocation,jdbcType=VARCHAR}


    and CREATE_TIME = #{createTime,jdbcType=TIMESTAMP}


    and DESCRIPTION = #{description,jdbcType=VARCHAR}


    and MEMBER_ID = #{memberId,jdbcType=INTEGER}


    and CITY_ID = #{cityId,jdbcType=INTEGER}
