mapper.select:

select VENDOR_ID, NAME, CONTACT_PERSON, CREATE_TIME, DEL_FLAG, EMAIL, MOBILE, DESCRIPTION
from rc_vendor
where VENDOR_ID = #{vendorId,jdbcType=INTEGER}


    select VENDOR_ID, NAME, CONTACT_PERSON, CREATE_TIME, DEL_FLAG, EMAIL, MOBILE, DESCRIPTION
    from rc_vendor


        and NAME like CONCAT('%', #{name,jdbcType=VARCHAR}, '%')


        and CONTACT_PERSON like CONCAT('%', #{contactPerson,jdbcType=VARCHAR}, '%')


        and CREATE_TIME = #{createTime,jdbcType=TIMESTAMP}


        and DEL_FLAG = #{delFlag,jdbcType=BIT}


        and EMAIL like CONCAT('%', #{email,jdbcType=VARCHAR}, '%')


        and MOBILE like CONCAT('%', #{mobile,jdbcType=VARCHAR}, '%')


        and DESCRIPTION = #{description,jdbcType=VARCHAR}



	  order by ${orderBy}


select VENDOR_ID, NAME, CONTACT_PERSON, CREATE_TIME, DEL_FLAG, EMAIL, MOBILE, DESCRIPTION
from rc_vendor


    and NAME = #{name,jdbcType=VARCHAR}


    and CONTACT_PERSON = #{contactPerson,jdbcType=VARCHAR}


    and CREATE_TIME = #{createTime,jdbcType=TIMESTAMP}


    and DEL_FLAG = #{delFlag,jdbcType=BIT}


    and EMAIL = #{email,jdbcType=VARCHAR}


    and MOBILE = #{mobile,jdbcType=VARCHAR}


    and DESCRIPTION = #{description,jdbcType=VARCHAR}


limit 1

mapper.delete:

delete from rc_vendor


    and NAME = #{name,jdbcType=VARCHAR}


    and CONTACT_PERSON = #{contactPerson,jdbcType=VARCHAR}


    and CREATE_TIME = #{createTime,jdbcType=TIMESTAMP}


    and DEL_FLAG = #{delFlag,jdbcType=BIT}


    and EMAIL = #{email,jdbcType=VARCHAR}


    and MOBILE = #{mobile,jdbcType=VARCHAR}


    and DESCRIPTION = #{description,jdbcType=VARCHAR}
